import { Component, Input } from '@angular/core';
import { FrameInfo } from '../models/frame-info'

@Component({
  selector: 'app-frame',
  templateUrl: './frame.component.html',
  styleUrls: ['./frame.component.css']
})
export class FrameComponent {
  @Input() frame: FrameInfo = new FrameInfo();
}
<div class="row">

  <div class="col-1">
    <input type="text" [(ngModel)]="frame.scores[0]" style="width: 24px;" />
  </div>
  <div class="col-1">
    <input type="text" [(ngModel)]="frame.scores[1]" style="width: 24px;" />
  </div>
  <div class="col-1" *ngif="frame.isLastFrame"> 
    <input *ngIf="frame.isLastFrame" type="text" style="width: 24px;" [(ngModel)]="frame.scores[2]" />
  </div>
</div>
<div class="row">
  <div class="col-1">
    <h2>{{frame.overallScore}}</h2>
  </div>
</div>
